import React, { useState } from 'react';
import Board from './components/Board';
import Modal from './components/Modal';
import './App.css';

const App: React.FC = () => {
  const [size, setSize] = useState(15);
  const [winner, setWinner] = useState<string | null>(null);
  const [reset, setReset] = useState(false);

  const handleWin = (winner: string) => {
    setWinner(winner);
  };

  const closeModal = () => {
    setWinner(null);
    setReset(true);
  };

  const onResetComplete = () => {
    setReset(false);
  };

  const handleReset = () => {
    setReset(true);
  };

  return (
    <div className="app-container">
      <h1>Крестики vs Нолики</h1>
      <Board size={size} onWin={handleWin} reset={reset} onResetComplete={onResetComplete} />
      <Modal show={winner !== null} message={`${winner} победили!`} onClose={closeModal} />
      <button className="reset-button" onClick={handleReset}>Очистить поле</button>
    </div>
  );
};

export default App;
