import React, { useState } from 'react';
import Board from './components/Board';
import Modal from './components/Modal';
import './App.css';

const App: React.FC = () => {
  const [size, setSize] = useState(1);
  const [winner, setWinner] = useState<string | null>(null);
  const [reset, setReset] = useState(false);

  const handleWin = (winner: string) => {
    setWinner(winner);
  };

  const closeModal = () => {
    setWinner(null);
    setReset(true);
  };

  const onResetComplete = () => {
    setReset(false);
  };

  return (
    <div className="app-container">
      <h1>Infinite Tic-Tac-Toe</h1>
      <Board size={size} onWin={handleWin} reset={reset} onResetComplete={onResetComplete} />
      <Modal show={winner !== null} message={`${winner} wins!`} onClose={closeModal} />
    </div>
  );
};

export default App;
