import React, { useState } from 'react';
import Board from './components/Board';
import Modal from './components/Modal';
import './App.css';

const App: React.FC = () => {
  const [size, setSize] = useState(20); // Размер игрового поля
  const [winner, setWinner] = useState<string | null>(null); // Победитель
  const [currentPlayer, setCurrentPlayer] = useState('X'); // Текущий игрок

  const handleWin = (winner: string) => {
    setWinner(winner);
  };

  const closeModal = () => {
    setWinner(null);
  };

  const switchPlayer = () => {
    setCurrentPlayer((prev) => (prev === 'X' ? 'O' : 'X'));
  };

  return (
    <div className="app-container">
      <h1>Бесконечные Крестики vs Нолики</h1>
      {/* Отображение текущего игрока */}
      <div className="current-player">
        <strong>Текущий ход: </strong>
        <span>{currentPlayer}</span>
      </div>
      {/* Игровое поле */}
      <Board
        size={size}
        onWin={handleWin}
        currentPlayer={currentPlayer}
        onPlayerSwitch={switchPlayer}
      />
      {/* Модальное окно при победе */}
      <Modal
        show={winner !== null}
        message={`${winner} победили!`}
        onClose={closeModal}
      />
    </div>
  );
};

export default App;
