import React, { useState } from 'react';
import Board from './components/Board';
import Modal from './components/Modal';
import './App.css';

const App: React.FC = () => {
  const [size, setSize] = useState(20);
  const [winner, setWinner] = useState<string | null>(null);
  const [currentPlayer, setCurrentPlayer] = useState<string>('X'); // Состояние текущего игрока

  const handleWin = (winner: string) => {
    setWinner(winner);
  };

  const closeModal = () => {
    setWinner(null);
  };

  const switchPlayer = () => {
    setCurrentPlayer(currentPlayer === 'X' ? 'O' : 'X'); // Переключение игрока
  };

  return (
    <div className="app-container">
      <h1>Бесконечные Крестики vs Нолики</h1>
      <div className="game-layout">
        <Board
          size={size}
          onWin={handleWin}
        />

        <div className="sidebar">
          <h2>Информация</h2>
          <p>
            <strong>Текущий ход:</strong> <span className="current-player">{currentPlayer}</span>
          </p>
          {winner && (
            <p>
              <strong>Победитель:</strong> {winner}
            </p>
          )}
        </div>
      </div>

      <Modal show={winner !== null} message={`${winner} победили!`} onClose={closeModal} />
    </div>
  );
};

export default App;
